mapper.select:
SELECT healthy_num, mem_id id, mem_num, healthy_title, healthy_content, h_reg_date, h_modify_date, healthy_hit, h_filename, fav_cnt, re_cnt FROM (SELECT rownum anum, a.* FROM (SELECT * FROM healthy_Blog JOIN member USING (mem_num) LEFT OUTER JOIN (SELECT COUNT(*) fav_cnt,healthy_num FROM healthy_fav GROUP BY healthy_num) USING(healthy_num) LEFT OUTER JOIN (SELECT COUNT(*) re_cnt,healthy_num FROM healthy_re GROUP BY healthy_num) USING(healthy_num)
ORDER BY healthy_num DESC ) a)
WHERE anum >= #{start} AND anum <= #{end}

SELECT COUNT(*)
FROM healthy_fav 
WHERE mem_num=#{mem_num}

SELECT COUNT(*)
FROM healthy_re_fav 
WHERE mem_num=#{mem_num}

SELECT COUNT(*)
FROM healthy_re 
WHERE mem_num=#{mem_num}

SELECT COUNT(*)
FROM healthy_blog 
WHERE mem_num=#{mem_num}
