mapper.select:
SELECT COUNT(*)
FROM review JOIN member USING(mem_num)
WHERE hos_num = #{hos_num}

SELECT COUNT(*)
FROM review JOIN member USING(mem_num)
WHERE mem_num = #{mem_num}

SELECT COUNT(*)
FROM review JOIN member USING(mem_num)
WHERE doc_num = #{doc_num}

SELECT 
	*
FROM (SELECT rownum anum, a.* 
			 FROM (SELECT * 
			 			  FROM review JOIN member USING(mem_num)
			 			  						  LEFT OUTER JOIN hospital USING(hos_num)
			 			  WHERE hos_num=#{hos_num}

			 			  ) a)

WHERE anum >= #{start} AND anum <= #{end}

